import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";
import { useState } from "react/cjs/react.development";

const NewExpense = (props) => {

  const [form, setForm] = useState(hidingForm)
  const saveExpenseDataHandler = (enteredExppenseData) => {
    console.log(enteredExppenseData)
    const expenseData = {
      ...enteredExppenseData,
      id: Math.random().toString()
    };
    props.onAddExpense(expenseData);
  };

  let hidingForm = (<button onClick={() => {
    setForm(<ExpenseForm onSaveExpenseData={saveExpenseDataHandler} setForm={setForm(form)} />)
  }}>Add New Expense</button>)

  

  return <div className="new-expense">
    {form}
  </div>;
};

export default NewExpense;
